function blogArticlePosition() {
    if (window.innerWidth >= 750) {
        const articles = document.querySelectorAll(".blog__article"),
            container = document.querySelector(".blog__articles");

        let leftColumnHeight = 0,
            rightColumnHeight = 0;

        articles.forEach((article, index) => {
            const height = article.offsetHeight + 35;
            if (index % 2 == 0) {
                article.style.left = "0";
                article.style.right = "";
                article.style.transform = "translateY(" + leftColumnHeight + "px)";
                leftColumnHeight += height;
            } else {
                article.style.left = "";
                article.style.right = "0";
                article.style.transform = "translateY(" + rightColumnHeight + "px)";
                rightColumnHeight += height;
            }
        });

        const containerHeight = Math.max(leftColumnHeight, rightColumnHeight);
        container.style.height = containerHeight + "px";
    }
}

